#include<stdio.h>#include<stdlib.h>#define ERROR(M) {fprintf(stderr, "%s\n", M); exit(1);}typedef struct listnode{  int data;  struct listnode* next;} listnode;typedef struct linkedlist{  listnode* head;  listnode* end;  int size;}linkedlist;//create listlinkedlist* createlist();//get methodsint getlength(linkedlist* list);int getvalue(linkedlist* list, int index);static listnode* getnode(linkedlist* list, int index);//add methodsvoid add(linkedlist* list, int index, int value);//changevalue mathodsvoid changevalue(linkedlist* list, int index,int value);//delete methodsvoid delete(linkedlist* list, int index);void freelist(linkedlist* list);//print methodsvoid printall(linkedlist* list);